import React, { useEffect, useState } from "react";
import {
  Link,
  NavLink,
  Route,
  Switch,
  useHistory,
  useRouteMatch,
} from "react-router-dom";
import { updateCard, createCard, readCard } from "../../utils/api";

export default function CardForm({ decks, setDecks, deck, setDeck, deckUrl }) {
  let decksTemp = decks;
  const deckTemp = deck;
  const [formData, setFormData] = useState({ front: "", back: "" });
  const history = useHistory();
  const { url, params, path } = useRouteMatch();
  const subUrls = url.split(`/`);
  let edit = false;
  // if edit then setFormdata to current card using card id from url
  useEffect(() => {
    if (subUrls[subUrls.length - 1] === "edit") {
      edit = true;
      readCard(subUrls[subUrls.length - 2]).then(setFormData);
    }
  }, []);

  function handleChange({ target }) {
    setFormData(() => ({
      ...formData,
      [target.name]: target.value,
    }));
  }
  function submitHandler(event) {
    event.preventDefault();
    const abortController = new AbortController();
    let handleFunction = createCard(deck.id, formData, abortController.signal);
    if (edit) {
      handleFunction = updateCard(formData, abortController.signal);
    }
    handleFunction // change to update card for edit
      .then(setFormData)
      .then(() => {
        deckTemp.cards = deckTemp.cards.filter(
          (card) => card.id != formData.id
        );
        deckTemp.cards.push(formData);
        setDeck(deckTemp);
      })
      .then(() => {
        decksTemp = decksTemp.filter((theDeck) => theDeck.id != deck.id);
        decksTemp.push(deckTemp);
        setDecks(decksTemp);
        if (!edit) {
          setFormData({ front: "", back: "" });
        } else {
          history.push(deckUrl);
        }
        // if edit got to view screen
      });
  }
  return (
    <form name="createDeck" onSubmit={submitHandler}>
      <div className="front">
        <label htmlFor="front">Front</label>
        <textarea
          id="front"
          type="text"
          name="front"
          value={formData.front}
          placeholder="Front side of card"
          className="form-control"
          onChange={handleChange}
        ></textarea>
      </div>
      <div className="form-group">
        <label htmlFor="back">Back</label>
        <textarea
          id="back"
          type="text"
          name="back"
          value={formData.back}
          placeholder="Back Side of card"
          className="form-control"
          onChange={handleChange}
        ></textarea>
      </div>
      <Link className="btn btn-secondary mr-1" to={deckUrl}>
        Done
      </Link>
      <button type="submit" className="btn btn-primary">
        Submit
      </button>
    </form>
  );
}
